BEGIN;

  TRUNCATE TABLE functions;


  ALTER TABLE src_preset
    ALTER COLUMN eq_band_1_level TYPE float,
    ALTER COLUMN eq_band_2_level TYPE float,
    ALTER COLUMN eq_band_3_level TYPE float,
    ALTER COLUMN eq_band_4_level TYPE float,
    ALTER COLUMN eq_band_5_level TYPE float,
    ALTER COLUMN eq_band_6_level TYPE float;

  ALTER TABLE module_config
    ALTER COLUMN eq_band_1_level TYPE float,
    ALTER COLUMN eq_band_2_level TYPE float,
    ALTER COLUMN eq_band_3_level TYPE float,
    ALTER COLUMN eq_band_4_level TYPE float,
    ALTER COLUMN eq_band_5_level TYPE float,
    ALTER COLUMN eq_band_6_level TYPE float;

  DROP VIEW matrix_sources;

  CREATE OR REPLACE VIEW matrix_sources (pos, type, number, label, active) AS
    SELECT 1, 'none', -1, 'mute', true
    UNION
     SELECT 2, 'none', 0, 'none', true
    UNION
     SELECT 2+pos, 'source', number+288, label, true
      FROM src_config
    UNION
     SELECT 2+number+(SELECT MAX(pos) FROM src_config), 'buss', number, label, true
      FROM buss_config
    UNION
     SELECT 2+number+(SELECT MAX(pos) FROM src_config)+16, 'monitor buss', number+16, label, number <= dsp_count()*4
      FROM monitor_buss_config
    UNION
     SELECT 2+g.n+(SELECT MAX(pos) FROM src_config)+32, 'insert out', g.n+32, 'Module '||g.n||' insert out', g.n <= dsp_count()*32
      FROM generate_series(1, 128) AS g(n)
    UNION
     SELECT 2+g.n+(SELECT MAX(pos) FROM src_config)+160, 'n-1', g.n+160, 'Module '||g.n||' N-1', g.n <= dsp_count()*32
      FROM generate_series(1, 128) AS g(n);

  ALTER TABLE extern_src_config
    ALTER COLUMN ext1 SET DEFAULT 0,
    ALTER COLUMN ext2 SET DEFAULT 0,
    ALTER COLUMN ext3 SET DEFAULT 0,
    ALTER COLUMN ext4 SET DEFAULT 0,
    ALTER COLUMN ext5 SET DEFAULT 0,
    ALTER COLUMN ext6 SET DEFAULT 0,
    ALTER COLUMN ext7 SET DEFAULT 0,
    ALTER COLUMN ext8 SET DEFAULT 0;

  ALTER TABLE talkback_config
    ALTER COLUMN source SET DEFAULT 0;

  UPDATE predefined_node_config SET func.seq =
  CASE
    WHEN (func).type = 5 THEN
      CASE
        WHEN (SELECT pos FROM src_config WHERE number = ((func).seq+1)) IS NULL THEN 1
        ELSE (SELECT pos FROM src_config WHERE number = ((func).seq+1))
      END
    WHEN (func).type = 6 THEN
      CASE
        WHEN (SELECT pos FROM dest_config WHERE number = ((func).seq+1)) IS NULL THEN 1
        ELSE (SELECT pos FROM dest_config WHERE number = ((func).seq+1))
      END
    ELSE (func).seq
  END;

  CREATE TABLE predefined_node_defaults (
    man_id smallint NOT NULL,
    prod_id smallint NOT NULL,
    firm_major smallint NOT NULL,
    cfg_name varchar(32) NOT NULL,
    object integer NOT NULL,
    data mambanet_data NOT NULL,
    PRIMARY KEY(man_id, prod_id, firm_major, cfg_name, object)
  );

  ALTER TABLE global_config
    ADD COLUMN date_time varchar(19) NOT NULL DEFAULT '0000-00-00 00:00:00';

  ALTER TABLE console_preset
    ADD COLUMN safe_recall_time float DEFAULT 1 NOT NULL CHECK(safe_recall_time>=0 AND safe_recall_time<=forced_recall_time),
    ADD COLUMN forced_recall_time float DEFAULT 3 NOT NULL CHECK(forced_recall_time>=safe_recall_time AND forced_recall_time<=10);

  ALTER TABLE src_config
    ADD COLUMN start_trigger smallint DEFAULT 0 NOT NULL CHECK(start_trigger>=0 AND start_trigger <=3),
    ADD COLUMN stop_trigger smallint DEFAULT 0 NOT NULL CHECK(stop_trigger>=0 AND stop_trigger <=3);

  ALTER TABLE node_config
    ADD COLUMN firm_major smallint DEFAULT 0 NOT NULL;

  ALTER TABLE defaults
    ADD COLUMN firm_major smallint DEFAULT 0 NOT NULL;

    UPDATE node_config n SET firm_major = (SELECT a.firm_major FROM addresses a WHERE a.addr = n.addr);
    UPDATE defaults d SET firm_major = (SELECT a.firm_major FROM addresses a WHERE a.addr = d.addr);

  ALTER TABLE node_config
    DROP CONSTRAINT node_config_pkey,
    ADD CONSTRAINT node_config_pkey PRIMARY KEY(addr, object, firm_major);

  ALTER TABLE defaults
    DROP CONSTRAINT defaults_pkey,
    ADD CONSTRAINT defaults_pkey PRIMARY KEY(addr, object, firm_major);

COMMIT;
